mapper.update:
UPDATE cc_rest_detail
   SET address = #{address},
   	   phone = #{phone},
   	   operating = #{operating},
   	   signature = #{signature}
 WHERE rid = #{rid}

mapper.select:
		SELECT t.*, @rowNum := @rownum + 1 as no
  		  FROM (
			SELECT article_id, rid, comment, likes, hate, write_dt, uid
	  		  FROM cc_article  		 
			 ORDER BY write_dt DESC
	 		 LIMIT #{firstIndex}, #{rowsPerPage}
			) t, (SELECT @rownum := #{firstIndex}) r;
