mapper.select:
SELECT *
  FROM cc_article
 WHERE rid=#{rid} and article_id=#{articleId}

SELECT IFNULL(MAX(article_id) + 1, 0)
  FROM cc_article
 WHERE rid=#{rid}

mapper.delete:
DELETE
  FROM cc_menu
 WHERE rid = #{rid} AND article_id = #{articleId}

DELETE
  FROM cc_tag
 WHERE rid = #{rid} AND article_id = #{articleId}

DELETE
  FROM cc_img
 WHERE rid = #{rid} AND article_id = #{articleId}
